// Target the image element
const changingImage = document.querySelectorAll(".service-desc h2 span");
// Start with the first image
let currentImageIndex = 1;

// Function to change the image
function changeImage() {
  const svglength = changingImage.length;

  // Find index of the active svg
  const activeIndex = Array.from(changingImage).findIndex((svg) =>
    svg.classList.contains("active")
  );

  // Remove 'active' class from all svg
  changingImage.forEach((svg) => svg.classList.remove("active"));

  if (activeIndex === 2) {
    changingImage[0].classList.add("active");
  } else {
    changingImage[activeIndex + 1].classList.add("active");
  }
}

// Change the image every 3 seconds
// setInterval(changeImage, 3000);

/**
 *
 * PLAY ANIMATION ON HOVER
 */
play_lottie();

/**
 *
 * TYPEWRITER ANIMAITON
 */

let typed = new Typed(".auto-type", {
  strings: ["terms", "time", "budget"],
  typeSpeed: 100,
  backSpeed: 100,
  loop: true,
});

/**
 *
 * LOOP ANIMATION 3 TIMES
 */

const target = document.querySelector(".CTA");
const lottieArrow = document.querySelector("#lottie-arrow");

const observer = new IntersectionObserver(
  (entries, observer) => {
    entries.forEach((entry) => {
      if (entry.isIntersecting) {
        // Check if the element is in the viewport
        lottieArrow.play();
      }
    });
  },
  {
    root: null, // null means it uses the viewport
    rootMargin: "0px",
    threshold: 1.0, // 1.0 means 100% of the target must be visible
  }
);

observer.observe(target);
